import "../Components_scss/Home.scss";
import React from 'react'
import { Link } from 'react-router-dom'
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore from 'swiper/core';  // SwiperCore만 가져옵니다.
import { Navigation, Pagination, Autoplay } from 'swiper';

// 'swiper/css'를 추가로 가져옵니다.
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';

SwiperCore.use([Navigation, Pagination, Autoplay]);

const Home = () => {
  return (
    <>
      <div className="HomeNav shadow-md border-t-2 border-gray-200 bg-white"></div>
      <div className="Home max-w-7xl mx-auto px-4 flex justify-end">
        {/* 실시간 검색어 */}
        <div className="describeMyself">
          <ul className="descriptionList">
            <li><b>1</b>Ambitious</li>
            <li><b>2</b>Promising</li>
            <li><b>3</b>Creative</li>
            <li><b>4</b>ambitious</li>
          </ul>
        </div>
        {/* 홈배너 */}
        <div className='page-container' style={{ fontSize: '32px' }}> {/* 's' 제거 */}
          <div className="link-container" style={{ marginTop: '30px' }}>
            <Link className="Link-Nav" to="/alcohol">술</Link>
            <Link className="Link-Nav" to="/review">리뷰</Link>
            <Link className="Link-Nav" to="/menu">메뉴</Link>
          </div>
          <div>
            <Swiper
              className="home-banner"
              spaceBetween={50}
              slidesPerView={1}
              navigation
              pagination={{ clickable: true }}
              autoplay={{ delay: 2000 }}>
              <SwiperSlide>
                <img className="home-img" src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2F8WQ5N%2Fbtshy5kxPYn%2FVJqr5MTjba7vLROZucajuk%2Fimg.png" alt="이미지다!!! " />
              </SwiperSlide>
              <SwiperSlide>
                <img className='home-img' src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2F1J1Il%2FbtshzBDtGf9%2F6h8SzcjwurvggUYcRO7UaK%2Fimg.png" alt="이미지다!!! " />
              </SwiperSlide>
              <SwiperSlide>
                <img className='home-img' src="https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=https%3A%2F%2Fblog.kakaocdn.net%2Fdn%2F3Rru3%2FbtshBmSUExv%2FRLgXbEOZhK4KVvs5TqsBKK%2Fimg.png" alt="이미지다!!! " />
              </SwiperSlide>
            </Swiper>
          </div>
        </div>
        {/* <div className="flex text-center bg-slate-600">
          <div className="text-4xl font-extrabold">홈화면이다.</div>
        </div> */}
      </div>
    </>
  );
}

export default Home;
