import "../Components_scss/Home.scss"
import React, { useRef, useState, MouseEvent, CSSProperties } from 'react';
import { Link } from 'react-router-dom'
import Slider from "react-slick"
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'


interface CloudyProps {
  hasCloudyArea?: boolean;
  cloudyAreaBgColor?: string;
}
interface Props extends CloudyProps {
  children: React.ReactNode;
}

/**
 * @since 2024.02.14
 * @author 최정윤
 */

const Home = ({ children, hasCloudyArea = false, cloudyAreaBgColor, ...rest }: Props) => {
  // 왼쪽 배너 세팅
  const leftsettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // 자동 재생 활성화
    autoplaySpeed: 6000, // 간격(ms) 설정 (예: 6초 = 6000ms)
  };

  // 오른쪽 배너 세팅
  const rightsettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true, // 자동 재생 활성화
    autoplaySpeed: 3000, // 간격(ms) 설정 (예: 3초 = 3000ms)
  };

  // 메뉴 슬라이드 세팅
  const containerRef = useRef<HTMLDivElement>(null);

  const [dragging, setDragging] = useState(false);
  const [clickPoint, setClickPoint] = useState(0);
  const [scrollLeft, setScrollLeft] = useState(0);

  const handleMouseDownEvent = (e: MouseEvent<HTMLDivElement>) => {
    setDragging(true);
    if (containerRef.current) {
      setClickPoint(e.pageX);
      setScrollLeft(containerRef.current.scrollLeft);
    }
  };

  const handleMouseMoveEvent = (e: MouseEvent<HTMLDivElement>) => {
    if (!dragging) return;

    e.preventDefault();

    if (containerRef.current) {
      const walk = e.pageX - clickPoint;

      containerRef.current.scrollLeft = scrollLeft - walk;
    }
  };

  return (
    <>
      <div className="HomeWrapper">
        {/* 홈 네비바
        @since 2024.02.14
        @author 홍길동 */}
        <div className="HomeNav shadow-md border-t-2 border-gray-200 bg-white">
          {/* 실시간 검색어 */}
          <div className="describeWrapper max-w-7xl mx-auto px-4">
            <div className="describeMyself ">
              <ul className="descriptionList">
                <li><b>1</b>Ambitious</li>
                <li><b>2</b>Promising</li>
                <li><b>3</b>Creative</li>
                <li><b>4</b>ambitious</li>
              </ul>
            </div>
          </div>
        </div>

        {/* 홈 배너
        @since 2024.02.15
        @author 최정윤 */}
        <div className="HomeBannerWrapper max-w-7xl mx-auto px-4">

          {/* 메인 슬라이드 배너 */}
          <div className="HomeBanner">
            <div className="HomeLeftSlider">
              {/* <h2> Single Item</h2> */}
              <Slider {...leftsettings}>
                <div className="HomeLeftSliderItem">
                  {/* <h3>1</h3> */}
                  {/* <img src="img/1.jpeg" title="pic"></img> */}
                  <div className="HomeLeftSliderItemContent">1</div>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>2</h3>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>3</h3>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>4</h3>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>5</h3>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>6</h3>
                </div>
                <div className="HomeLeftSliderItem">
                  <h3>7</h3>
                </div>
              </Slider>
            </div>

            {/* 사이드 슬라이드 배너 */}
            <div className="HomeRightSlider">
              {/* <h2> Single Item</h2> */}
              <Slider {...rightsettings}>
                <div className="HomeRightSliderItem">
                  <div className="HomeRightSliderItemContent">1</div>
                </div>
                <div className="HomeRightSliderItem">
                  <h3>2</h3>
                </div>
                <div className="HomeRightSliderItem">
                  <h3>3</h3>
                </div>
                <div className="HomeRightSliderItem">
                  <h3>4</h3>
                </div>
                <div className="HomeRightSliderItem">
                  <h3>5</h3>
                </div>
                <div className="HomeRightSliderItem">
                  <h3>6</h3>
                </div>
              </Slider>
            </div>
          </div>
        </div>
        {/* 홈 카테고리
        @since 2024.02.19
        @author 최정윤 */}
        <div className="" {...rest}>
          {hasCloudyArea && <CloudyArea cloudyAreaBgColor={cloudyAreaBgColor} />}
          <Slider
            ref={containerRef}
            onMouseDown={handleMouseDownEvent}
            onMouseLeave={() => setDragging(false)}
            onMouseUp={() => setDragging(false)}
            onMouseMove={handleMouseMoveEvent}
          >
            {children}
          </Slider>
        </div>
      </div>
    </>
  );
}

export default Home;
