import Navbar from "./Navbar";
interface CategoryItem {
  link: string;
  imageSrc: string;
  name: string;
}

const categories: CategoryItem[] = [
  {
    link: 'https://living.hyundailivart.co.kr/c/C200000059?pagecode=D100000479&pagecode=D100000483',
    imageSrc: 'https://static.hyundailivart.co.kr/upload_mall/view/V000000127/D100000483/P000000455_S100016075.png?20231212112729',
    name: '소파',
  },
  {
    link: 'https://living.hyundailivart.co.kr/c/C200000061?pagecode=D100000479&pagecode=D100000483',
    imageSrc: 'https://static.hyundailivart.co.kr/upload_mall/view/V000000127/D100000483/P000000455_S100016077.png?20231212112729',
    name: '식탁',
  },
  // ... (다른 카테고리도 동일하게 추가)
];
const Home = () => {
  return (
    <div className="Home">
      {/* content goes here */}
      {/* <div className="py-32 text-center">
        <div className="text-4xl font-extrabold">홈화면이다.</div>
      </div> */}
      <section id="lstore-category-listsD100000483" className="section-main section-category-lists swiper-control-wrap is-interval mb5">
        <div className="section-title-wrap sr-only">
          <h3 className="section-title"></h3>
        </div>
        {/* Add Arrows & Add Pagination (swiper-control-wrap과 같이 사용) */}
        <div className="section-category-control-wrap">
          <div className="swiper-button-prev swiper-button-disabled" tabIndex={0} role="button" aria-label="Previous slide" aria-disabled="true"></div>
          <div className="swiper-button-next" tabIndex={0} role="button" aria-label="Next slide" aria-disabled="false"></div>
        </div>
        <div className="container swiper-container swiper-container-initialized swiper-container-horizontal">
          {/* li 갯수가 9개 이상일때만 slider 동작 */}
          <ul className="swiper-wrapper" style={{ transform: 'translate3d(0px, 0px, 0px)' }}>
            {categories.map((category, index) => (
              <li key={index} className={`swiper-slide ${index === 0 ? 'swiper-slide-active' : ''}`} style={{ width: '127.5px' }}>
                <a href={category.link}>
                  <span className="pic">
                    <img className="img" src={category.imageSrc} alt="" />
                  </span>
                  <span className="name">{category.name}</span>
                </a>
              </li>
            ))}
          </ul>
          <span className="swiper-notification" aria-live="assertive" aria-atomic="true"></span>
        </div>
      </section>
    </div>
  );
}

export default Home;
