import "../Components_scss/Home.scss";
import React from 'react'
import { Link } from 'react-router-dom'

const Home = () => {
  return (
    <>
      <div className="HomeNav shadow-md border-t-2 border-gray-200 bg-white"></div>
      <div className="Home max-w-7xl mx-auto px-4 flex justify-end">
        {/* 실시간 검색어 */}
        <div className="describeMyself">
          <ul className="descriptionList">
            <li><b>1</b>Ambitious</li>
            <li><b>2</b>Promising</li>
            <li><b>3</b>Creative</li>
            <li><b>4</b>ambitious</li>
          </ul>
        </div>
        {/* 홈배너 */}
        // 1번 div
<div className="relative overflow-hidden z-[1] group" ref={outRef}>
  <SlideBtn direction="left" onClick={() => slideHandler(-1)} />
  <SlideBtn direction="right" onClick={() => slideHandler(1)} />
  <Pagination setSlideIndex={setSlideIndex} slideIndex={slideIndex} />
  // 2번 div
  <div
	className="flex"
	ref={slideRef}
	style={{
		width: `${100 * COPIED_NUM}vw`,
		transition: "all 500ms ease-in-out",
		transform: `translateX(${
			-1 * ((100 / copiedArr.length) * slideIndex)
		}%)`,
	}}
  >
  {copiedArr.map((item, index) => (
	// 3번 div
	<div key={index} className="relative">
	  <img
		src={item.img}
		alt="banner"
		className="h-[500px] sm:h-[655px] lg:w-screen lg:h-[700px]"
	  />

	  <div className="absolute px-[5%] font-bold text-white top-1/2">
		<h1 className="text-[24px] lg:text-[28px]">{item.imgTitle}</h1>
		<p className="text-[36px] lg:text-[52px]">{item.imgDesc}</p>
	  </div>
	</div>
	))}
  </div>
</div>
        {/* <div className="flex text-center bg-slate-600">
          <div className="text-4xl font-extrabold">홈화면이다.</div>
        </div> */}
      </div>
    </>
  );
}

export default Home;
